import Foundation
import Coinpaprika

final class BuyCoinsPresenter {

    // MARK: - Private properties -

    private unowned let view: BuyCoinsViewInterface
    private let wireframe: BuyCoinsWireframeInterface
    private let interactor: BuyCoinsInteractorInterface

    private let coinId: String
    // MARK: - Lifecycle -

    init(view: BuyCoinsViewInterface, wireframe: BuyCoinsWireframeInterface, interactor: BuyCoinsInteractorInterface, coinId: String) {
        self.view = view
        self.wireframe = wireframe
        self.interactor = interactor
        
        self.coinId = coinId
    }
    
    func didGetCoinRate(coinId: String, usdAmount: Int, convertionRate: Float, boughtPrice: Float) {
        let coinAmount = Float(usdAmount) * convertionRate
        
        let state = interactor.buyCoin(coinId: coinId, coinAmount: coinAmount, usdAmount: usdAmount, boughtPrice: boughtPrice)
        didBuyCoin(success: state)
    }
}

// MARK: - Extensions -

extension BuyCoinsPresenter: BuyCoinsPresenterInterface {
    
    func buyCoin(usdAmount: String?) {
        if let amountText = usdAmount, let amount = Int(amountText)  {
            interactor.getConvertionRateFromApi(for: coinId, usdAmount: amount, convertionCallBack: didGetCoinRate)
        } else {
            didBuyCoin(success: false)
        }
    }
    
    func didBuyCoin(success: Bool) {
        view.didBoughtCoin(success: success)
    }
 
    func getCoinInfos() {
        interactor.getCoinInfosFromApi(for: coinId, storeCoin: didGetCoinInfos(ticker:))
    }
    
    func didGetCoinInfos(ticker: Ticker) {
        view.updateCoinView(coinInfos: ticker)
    }
    
    func getCoinOhlcv(from date: Date) {
        
        interactor.getCoinOhlcvFromApi(for: coinId, from: date, storeCoinOhlvc: didGetCoinOhlcv(ohlcv:))
    }
    
    func didGetCoinOhlcv(ohlcv: [Ohlcv]) {
        view.updateCoinView(coinOhlcv: ohlcv)
    }
}
